from django.shortcuts import render

from app.forms import CreatePokemonForm


def create(request):
    if request.GET:
        form = CreatePokemonForm(request.GET)
        if form.is_valid():
            creator = form['Creator']
            name = form['name']
            height = form['height']
            weight = form['weight']
            region = form['region']
            description = form['description']
            type = form['type']
            type2 = form['type2']
            return render (request, "create.html", {"form":form, "creator":creator, "name":name, "height":height, "weight":weight, "region":region, "description":description, "type":type, "type2":type2})
    else:
        form=CreatePokemonForm()
    return render(request, "create.html", {"form":form})

